mapper.insert:
INSERT INTO t_board
(typ, seq, title, ctnt, i_user)
SELECT #{typ}, IFNULL(MAX(seq), 0) +1, #{title}, #{ctnt}, #{i_user}
FROM t_board WHERE typ = #{typ}

mapper.update:
UPDATE t_board

	title= #{title},
	ctnt= #{ctnt}
	hits = hits + 1

WHERE i_board= #{i_board}
AND i_user = #{i_user}

UPDATE t_board
SET hits = hits + 1
WHERE i_board = #{i_board}

mapper.delete:
DELETE FROM t_board
WHERE i_board = #{i_board}
AND i_user = #{i_user}
